mapper.select:
select fh.id, fh.html_name as htmlName, fh.html_type as htmlType, fh.html_keyword as htmlKeyword, fh.user_id as userId, fh.take_effect as takeEffect, fh.html_url as htmlUrl, fh.create_date as createDate, fh.update_date as updateDate from front_html fh
and fh.html_name like '%${htmlName}%'
and fh.html_type = #{htmlType}
and fh.html_keyword like '%${htmlKeyword}%'
and fh.take_effect = #{takeEffect}
order by fh.id desc
